			clockwise = entrance.y < center.y;
			if (entrance.x  == right) clockwise = !clockwise;
		} else {
			door.y = center.y;
			clockwise = entrance.x > center.x;
			if (entrance.y == bottom) clockwise = !clockwise;
		}

