public void addRewardItem(Generator.Category cat, ArrayList<Item> items, ArrayList<Item> dupes){
		while (true) {
			Item reward = Generator.random(cat);

			boolean dupe = false;
			for (Item i : items){
				if (i.isSimilar(reward)){
					dupes.add(reward);
					dupe = true;
					break;
				}
			}

			if (!dupe){
				items.add(reward);
				return;
			}
		}
	}

