					map[corner] = Terrain.WALL;
					level.traps.remove(corner);
				}
			}

		}

		for (int i=w + 1; i < l - w; i++) {
			if (map[i] == Terrain.EMPTY) {
				int n = 0;
				if (map[i+1] == Terrain.WALL) {
