private static int runTypeOfs(){
		if (Dungeon.daily){
			if (Dungeon.dailyReplay){
				return 24;
			} else {
				return 16;
			}
		} else if (!Dungeon.customSeedText.isEmpty()){
			return 8;
		} else {
			return 0;
		}
	}

