public static void show( Char ch, int index ) {

		if (!ch.sprite.visible) {
			return;
		}

		SpellSprite old = all.get( ch );
		if (old != null) {
			old.kill();
		}

		SpellSprite sprite = GameScene.spellSprite();
		sprite.target = ch;
		sprite.reset( index );
		sprite.revive();
		all.put( ch, sprite );
	}

