			if (Dungeon.level.adjacent( pos, enemy.pos )) {

				return super.doAttack( enemy );

			} else {

				if (sprite != null && (sprite.visible || enemy.sprite.visible)) {
					sprite.zap( enemy.pos );
					return false;
				} else {
					zap();
					return true;
				}
			}
		}

