						if (!wall(cell - 1) && !door(cell - 1)){
							if (fogHidden(cell - 1)) {
								curr += 2;
							}
						} else {
							if (fogHidden(cell - 1 + mapWidth)){
								curr += 2;
							}
						}

					}

				}

			} else {
				curr = BLOCK_NONE;

				if ((cell + 1) % mapWidth != 0 && fogHidden(cell + 1)) {
					curr += 1;
				}

				if (cell % mapWidth != 0 && fogHidden(cell - 1)) {
					curr += 2;
				}
			}

		} else {
			curr = BLOCK_NONE;
		}

		if (prev != curr){
			data[cell] = curr;
			super.updateMapCell(cell);
		}
	}

