@Override
		public boolean act() {
			if (armor == null) detach();
			else if (armor.isEquipped((Hero)target)) {
				int maxShield = 1 + armor.tier + armor.level();
				if (target.SHLD < maxShield){
					partialShield += 1/(30*Math.pow(0.9f, (maxShield - target.SHLD)));
				}
			}
			while (partialShield >= 1){
				target.SHLD++;
				partialShield--;
			}
			spend(TICK);
			return true;
		}

