@Override
	protected boolean act() {

		super.act();

		boolean alertedNow = alerted;
		alerted = false;

		sprite.hideAlert();

		if (paralysed) {
			enemySeen = false;
			spend( TICK );
			return true;
		}

		enemy = chooseEnemy();

		boolean enemyInFOV = enemy.isAlive() && Level.fieldOfView[enemy.pos] && enemy.invisible <= 0;

		int oldPos = pos;

		switch (state) {

		case SLEEPING:
			if (enemyInFOV && 
				Random.Int( distance( enemy ) + enemy.stealth() + (enemy.flying ? 2 : 0) ) == 0) {

				enemySeen = true;

				notice();
				state = State.HUNTING;
				target = enemy.pos;

				spend( TIME_TO_WAKE_UP );

			} else {

				enemySeen = false;

				spend( TICK );

			}
			return true;

		case WANDERING:
			if (enemyInFOV && (alertedNow || Random.Int( distance( enemy ) / 2 + enemy.stealth() ) == 0)) {

				enemySeen = true;

				notice();
				state = State.HUNTING;
				target = enemy.pos;

			} else {

				enemySeen = false;

				if (target != -1 && getCloser( target )) {
					spend( 1 / speed() );
					return moveSprite( oldPos, pos );
				} else {
					target = Dungeon.level.randomDestination();
					spend( TICK );
				}

			}
			return true;

		case HUNTING:
			enemySeen = enemyInFOV;
			if (enemyInFOV && canAttack( enemy )) {

				return doAttack( enemy );

			} else {

				if (enemyInFOV) {
					target = enemy.pos;
				}

				if (target != -1 && getCloser( target )) {


					spend( 1 / speed() );
					return moveSprite( oldPos,  pos );

				} else {

					spend( TICK );
					state = State.WANDERING;
					target = Dungeon.level.randomDestination();	// <--------
					return true;
				}
			}

		case FLEEING:
			enemySeen = enemyInFOV;
			if (enemyInFOV) {
				target = enemy.pos;
			}
			if (target != -1 && getFurther( target )) {

				spend( 1 / speed() );
				return moveSprite( oldPos, pos );

			} else {

				spend( TICK );
				nowhereToRun();

				return true;
			}

		case PASSIVE:
			enemySeen = false;
			spend( TICK );
			return true;

		}

		return true;

	}

